mapper.update:
update  LSHC_BUILD_INFO
set DELETED =1,ISVALIDE='0', VERSION=VERSION+1,UPDATE_TIME=(select sysdate  from dual),UPDATE_ID=#{userId}
where id in

  #{id}

mapper.select:
SELECT DISTINCT t1.id, t1.SERIAL_NO AS serialNo, t1.REGION AS region, t1.STREET AS street, t1.PROJECT_NAME AS projectName, t1.MAP_CODE AS mapCode, t1.ADDRESS AS address, count( t2.id ) over ( partition BY t1.id ) AS houseCount FROM LSHC_BUILD_INFO t1 LEFT JOIN LSHC_REGI_INFO t2 ON t1.MAP_CODE = t2.MAP_CODE
INNER JOIN BUSINESS_TASK t3 ON t2.id = t3.business_id
INNER JOIN job_log t3 ON t2.id = t3.business_id AND t3.is_deleted = 0
WHERE t1.ISVALIDE = '1' AND t1.DELETED = 0 AND t2.ISVALIDE = '1' AND t2.DELETED =0
AND t2.house_status in ('0','3')
and t2.org_code in
#{authority.dm}
AND t3.owner_type = #{ownerType} AND t3.owner in
#{item.id}
AND t3.auditor = #{ userId}
AND t1.REGION = #{region}
AND t1.STREET = #{street}
AND t1.PROJECT_NAME LIKE '%'||#{projectName}||'%'
AND t1.MAP_CODE LIKE '%'||#{mapCode}||'%'
AND t1.ADDRESS LIKE '%'||#{address}||'%'
ORDER BY t1.SERIAL_NO
